mapper.update:
UPDATE App
set appName = #{appName},companyID = #{companyID},appURL = #{appURL}
,appImageIconPath = #{appImageIconPath},appImageHBannerPath = #{appImageHBannerPath}
,appImageVBannerPath = #{appImageVBannerPath},appPackage = #{appPackage},appEnable = #{appEnable}
where appID = #{appID}

UPDATE App
set appKey = #{appKey}
where appID = #{appID}
